mapper.select:
select * from(
select
demand_id,demand_code,is_change,od.create_time,od.create_user_id,od.create_user_name,
demand_source,data_source,demand_state,org.org_name as demand_org_id,demand_direction,od.wbs_code,wbs_id,
od.wbs_name,reason,change_reason,
ROW_NUMBER() OVER(order by od.create_time desc) r
from
out_demand_plan od
left join sys_org org
on od.demand_org_id=org.org_id



            and demand_state = #{DemandPlanCondition.demandState}


            and demand_code = #{DemandPlanCondition.demandCode}


            and demand_source = #{DemandPlanCondition.demandSource}


            and od.create_time >= #{DemandPlanCondition.createStartTime}


            and od.create_time <= #{DemandPlanCondition.createEndTime}


            and od.create_user_name = #{DemandPlanCondition.createUserName}


            and demand_org_id = #{DemandPlanCondition.demandOrgId}


            and od.wbs_name = #{DemandPlanCondition.wbsName}



)s
where  s.r between #{page.startPos} and ((#{page.pageSize}+#{page.startPos})-1)

select count(*)
from
out_demand_plan od



            and demand_state = #{DemandPlanCondition.demandState}


            and demand_code = #{DemandPlanCondition.demandCode}


            and demand_source = #{DemandPlanCondition.demandSource}


            and create_time >= #{DemandPlanCondition.createStartTime}


            and create_time <= #{DemandPlanCondition.createEndTime}


            and create_user_name = #{DemandPlanCondition.createUserName}


            and demand_org_id = #{DemandPlanCondition.demandOrgId}


            and wbs_name = #{DemandPlanCondition.wbsName}

mapper.update:
update out_demand_plan
demand_source = #{demandSource},data_source = #{dataSource}, demand_org_id = #{demandOrgId}, demand_direction = #{demandDirection}, demand_state = #{demandState}, is_change = #{isChange}, reason = #{reason}, wbs_code = #{wbsCode}, wbs_name = #{wbsName}, wbs_id = #{wbsId}, change_reason = #{changeReason}
where demand_id = #{demandId}
