mapper.insert:
insert into out_pick_notice_detail (pick_detail_id,demandplan,demanddetail,pick_num,wbs,materials,pick_notice) SELECT A.* FROM(
(SELECT #{item.pickDetailId,jdbcType=VARCHAR} pick_detail_id, #{item.demandplan.demandId,jdbcType=VARCHAR} demandplan, #{item.demanddetail.demandDetailId,jdbcType=VARCHAR} demanddetail, #{item.pickNum,jdbcType=VARCHAR} pick_num, #{item.wbs.projectId,jdbcType=CHAR} wbs, #{item.materials.materialsId,jdbcType=CHAR} materials, #{item.pickNotice.pickId,jdbcType=CHAR} pick_notice FROM dual)
)A

mapper.select:
select * from out_pick_notice_detail opnd
left join out_pick_notice opn on opnd.pick_notice = opn.pick_id
left join b_materials bm on opnd.materials = bm.materials_id

    opn.pick_notice_state = 'pickNoticeCommit'
    and opn.supplier = #{PickNoticeCondition.supplierId}
    and opn.pick_no = #{PickNoticeCondition.pickNoticeNo}
    and opnd.wbs = #{PickNoticeCondition.wbsId}
    and
        opn.create_user_name like '%'|| #{PickNoticeCondition.createUserName} || '%'

    and
         opn.create_time  >=  #{PickNoticeCondition.beginTime}  
    and
         opn.create_time  <= #{PickNoticeCondition.endTime}  
    and bm.materials_code like '%' || #{PickNoticeCondition.materialsCode} || '%'
    and bm.materials_describe like '%' || #{PickNoticeCondition.materialsDescribe} || '%'
