mapper.select:
select * from( select warehouse_id,warehouse_code,warehouse_name,country.dictionary_name as country, city.dictionary_name as city,area.dictionary_name as area,acreage,dic.dictionary_name as warehouse_type,contacts,phone,fax,remark, ROW_NUMBER() OVER(order by w.warehouse_id) r from b_warehouse w left join sys_dictionary dic on dic.dictionary_code = w.warehouse_type left join sys_dictionary country on country.dictionary_code = w.country left join sys_dictionary city on city.dictionary_code = w.city left join sys_dictionary area on area.dictionary_code = w.area
dic.is_del ='1' and (country.is_del='1' or country.is_del is null) and (city.is_del='1' or country.is_del is null) and (area.is_del='1' or country.is_del is null)
and w.warehouse_code = #{WareHouseCondition.warehouseCode}
and w.warehouse_name = #{WareHouseCondition.warehouseName}
and w.warehouse_type = #{WareHouseCondition.warehouseType}
and w.warehouse_state = #{WareHouseCondition.warehouseState}
and w.warehouse_id in
#{item}
) s where s.r between #{page.startPos} and ((#{page.pageSize}+#{page.startPos})-1)

select count(*) from b_warehouse w
left join sys_dictionary dic
on dic.dictionary_code = w.warehouse_type
left join sys_dictionary country
on country.dictionary_code = w.country
left join sys_dictionary city
on city.dictionary_code = w.city
left join sys_dictionary area
on area.dictionary_code = w.area

	dic.is_del ='1' and (country.is_del='1' or country.is_del is null) and (city.is_del='1' or country.is_del is null) and (area.is_del='1' or country.is_del is null)

			and w.warehouse_code = #{WareHouseCondition.warehouseCode}


			and w.warehouse_name = #{WareHouseCondition.warehouseName}


			and w.warehouse_type = #{WareHouseCondition.warehouseType}


			and w.warehouse_state = #{WareHouseCondition.warehouseState}


			 and w.warehouse_id in

		      #{item}

mapper.update:
update b_warehouse
country = #{country}, city = #{city}, area = #{area}, acreage = #{acreage}, warehouse_type = #{warehouseType}, contacts = #{contacts}, phone = #{phone}, fax = #{fax}, remark = #{remark}, org_id = #{orgId}
where warehouse_id = #{warehouseId}

mapper.select:
select count(*) from b_warehouse se 

   se.warehouse_code=#{stoNo} 
   and se.warehouse_id != #{stoId}
