mapper.select:
select count(*) from out_warehouse ow left join sys_org so on so.org_id = ow.contractor left join sys_dictionary sd on ow.out_warehouse_state = sd.dictionary_code
and ow.out_warehouse_no like '%'|| #{OutWarehouseCondition.outWarehouseNo} || '%'
and ow.contractor = #{OutWarehouseCondition.supplierId} and ow.out_warehouse_state = #{OutWarehouseCondition.outWarehouseState}
and ow.create_user_name like '%'|| #{OutWarehouseCondition.createUserName} || '%'
and ow.create_time >= #{OutWarehouseCondition.beginTime} and ow.create_time <= #{OutWarehouseCondition.endTime}

select * from ( select ow.out_warehouse_id,ow.out_warehouse_no,ow.out_time,ow.create_user_name,ow.create_time,ow.contractor,sd.dictionary_name out_warehouse_state,ROW_NUMBER() OVER(order by ow.create_time desc) r from out_warehouse ow left join sys_org so on so.org_id = ow.contractor left join sys_dictionary sd on ow.out_warehouse_state = sd.dictionary_code
and ow.out_warehouse_no like '%'|| #{OutWarehouseCondition.outWarehouseNo} || '%'
and ow.contractor = #{OutWarehouseCondition.supplierId} and ow.out_warehouse_state = #{OutWarehouseCondition.outWarehouseState}
and ow.create_user_name like '%'|| #{OutWarehouseCondition.createUserName} || '%'
and ow.create_time >= #{OutWarehouseCondition.beginTime} and ow.create_time <= #{OutWarehouseCondition.endTime}
) s where s.r between #{page.startPos} and ((#{page.pageSize}+#{page.startPos})-1)

mapper.update:
update out_warehouse

    out_time = #{outTime},
    contractor = #{contractor.orgId},
    out_warehouse_state = #{outWarehouseState},
    out_warehouse_id = #{outWarehouseId}


    out_warehouse_id = #{outWarehouseId}
